              height: 60, // 배너 및 버튼 높이
            },
          });
          naverLogin.init();
          naverLogin.getLoginStatus((status) => {
            console.log("Naver 로그인 상태" + status);
            if (status) {
              console.log("Naver 로그인 상태", naverLogin.user);
              sessionStorage.setItem("id", naverLogin.user.getEmail());
            }
          });
